import React, { useState, useEffect } from 'react';

const baseUrl = 'https://projectfinancetracker-backend-2f2604a2f7f0.herokuapp.com';

const ExpenseFetcher = () => {
  const [expenses, setExpenses] = useState([]);
  const [newExpense, setNewExpense] = useState({
    description: '',
    amount: '',
    category: '',
    date: '',
    quantity: 0,
    unit: '',
    unitPrice: ''
  });

  useEffect(() => {
    const fetchExpenses = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await fetch(`${baseUrl}/expenses`, {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`,
          }
        });
        const data = await response.json();
        if (response.ok) {
          setExpenses(data);
        } else {
          throw new Error(data.message || "Failed to fetch expenses");
        }
      } catch (error) {
        console.error("Error fetching expenses:", error);
      }
    };

    fetchExpenses();
  }, []);

  const handleAddExpense = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    try {
      const response = await fetch(`${baseUrl}/expenses`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(newExpense),
      });
      const addedExpense = await response.json();
      if (response.ok) {
        setExpenses([...expenses, addedExpense]);
        setNewExpense({
          description: '',
          amount: '',
          category: '',
          date: '',
          quantity: 0,
          unit: '',
          unitPrice: ''
        }); // Reset the form
      } else {
        throw new Error(addedExpense.message || "Failed to add expense");
      }
    } catch (error) {
      console.error("Error adding expense:", error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewExpense(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div>
      <h2>My Expenses</h2>
      <ul>
        {expenses.map(expense => (
          <li key={expense._id}>{`${expense.description}: $${expense.amount}`}</li>
        ))}
      </ul>
      <form onSubmit={handleAddExpense}>
        <input
          type="text"
          name="description"
          value={newExpense.description}
          onChange={handleInputChange}
          placeholder="Description"
          required
        />
        {/* Include other fields such as amount, category, date, etc., following the same pattern */}
        <button type="submit">Add Expense</button>
      </form>
    </div>
  );
};

export default ExpenseFetcher;
