import React from "react";
import { Outlet, Link } from "react-router-dom";
import { useAuth } from './AuthContext'; // Import useAuth only

function HomePage() {
    const { isLoggedIn, logout } = useAuth(); // Use camelCase for the logout function and ensure it matches your context
    return (
        <>
            <nav>
                {isLoggedIn ? (
                    <div>Welcome, user! <button onClick={logout}>Logout</button></div> // Use logout here
                ) : (
                    <div>Please log in or register.</div>
                )}
                <ul>
                    <li>
                        <Link to="/">Home</Link>
                    </li>
                    <li>
                        <Link to="/login">Login</Link>
                    </li>
                    <li>
                        <Link to="/register">Register</Link>
                    </li>
                </ul>
            </nav>

            <Outlet />
        </>
    );
}

export default HomePage;
