// HomePage.js example
import { Outlet, Link } from "react-router-dom";

import React, { useState } from 'react';
const baseUrl = 'http://localhost:3001'; // Adjust according to your backend server

function Register() {
  // State for registration form
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent the form from submitting the traditional way
  
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    };
  
    try {
      // Update the fetch URL to your backend endpoint for registration
      const response = await fetch(`${baseUrl}/authRoutes/register`, requestOptions);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
  
      // Handle response data
      console.log(data);
      // Redirect the user or show a success message
      // For example, if login is successful, you might want to redirect
      // window.location.href = '/login'; // Redirect to login page
    } catch (error) {
      console.error('Error during registration:', error);
      // Display error message to user
    }
  };

  return (
    <div className="register-container">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default Register;
