import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Button, Container, TextField, Typography, Paper, Alert } from '@mui/material';

const baseUrl = 'https://projectfinancetracker-backend-2f2604a2f7f0.herokuapp.com'; // Adjust according to your backend server

function Register() {
  const navigate = useNavigate();
  // State for registration form and error message
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });
  const [error, setError] = useState('');

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Clear out any previous errors
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    };

    try {
      const response = await fetch(`${baseUrl}/auth/register`, requestOptions);
      if (!response.ok) {
        // Attempt to read the server response for error details
        const errorData = await response.json();
        throw new Error(errorData.message || 'Something went wrong, please try again.');
      }
      const data = await response.json();

      // Assuming 'data.token' is your token and 'data.user.id' exists
      localStorage.setItem('token', data.token);
      localStorage.setItem('isLoggedIn', true);
      localStorage.setItem('fiscalfoxID', data.user.id);
      navigate('/');
    } catch (error) {
      setError(error.message);
      console.error('Error during registration:', error);
    }
  };

  return (
    <Container component="main" maxWidth="xs">
      <Paper elevation={3} sx={{ marginTop: 8, display: 'flex', flexDirection: 'column', alignItems: 'center', padding: 2 }}>
        <Typography component="h1" variant="h5">
          Sign Up
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
          {error && <Alert severity="error" sx={{ width: '100%', mb: 2 }}>{error}</Alert>}
          <TextField
            // ...rest of your TextField props
          />
          <TextField
            // ...rest of your TextField props
          />
          <TextField
            // ...rest of your TextField props
          />
          <Button
            // ...rest of your Button props
          >
            Sign Up
          </Button>
          <Button
            // ...rest of your Button props
          >
            Already have an account? Login
          </Button>
        </Box>
      </Paper>
    </Container>
  );
}

export default Register;
